SELECT SUM(CASE WHEN TRIM(UPPER(PROD.NUMERO_POLIZA))!=TRIM(UPPER(UAT.NUMERO_POLIZA)) THEN 1 ELSE 0 END) AS DIFF_NUMERO_POLIZA
,SUM(CASE WHEN PROD.OPERATIONPK!=UAT.OPERATIONPK THEN 1 ELSE 0 END) AS DIFF_OPERATIONPK
,SUM(CASE WHEN TRIM(UPPER(PROD.MONEDA))!=TRIM(UPPER(UAT.MONEDA)) THEN 1 ELSE 0 END) AS DIFF_MONEDA
,SUM(CASE WHEN TRIM(UPPER(PROD.CODIGO_COBERTURA))!=TRIM(UPPER(UAT.CODIGO_COBERTURA)) THEN 1 ELSE 0 END) AS DIFF_CODIGO_COBERTURA
,SUM(CASE WHEN TRIM(UPPER(PROD.NOMBRE_COBERTURA))!=TRIM(UPPER(UAT.NOMBRE_COBERTURA)) THEN 1 ELSE 0 END) AS DIFF_NOMBRE_COBERTURA
,SUM(CASE WHEN PROD.ID_PRODUCTO!=UAT.ID_PRODUCTO THEN 1 ELSE 0 END) AS DIFF_ID_PRODUCTO
,SUM(CASE WHEN TRIM(UPPER(PROD.COBERTURA_PRIMA_NETA))!=TRIM(UPPER(UAT.COBERTURA_PRIMA_NETA)) THEN 1 ELSE 0 END) AS DIFF_COBERTURA_PRIMA_NETA
,SUM(CASE WHEN TRIM(UPPER(PROD.COBERTURA_PRIMA))!=TRIM(UPPER(UAT.COBERTURA_PRIMA)) THEN 1 ELSE 0 END) AS DIFF_COBERTURA_PRIMA
,SUM(CASE WHEN TRIM(UPPER(PROD.PAGO))!=TRIM(UPPER(UAT.PAGO)) THEN 1 ELSE 0 END) AS DIFF_PAGO
,SUM(CASE WHEN TRIM(UPPER(PROD.FRECUENCIA_PAGO))!=TRIM(UPPER(UAT.FRECUENCIA_PAGO)) THEN 1 ELSE 0 END) AS DIFF_FRECUENCIA_PAGO
,SUM(CASE WHEN TRIM(UPPER(PROD.FECHA_EMISION))!=TRIM(UPPER(UAT.FECHA_EMISION)) THEN 1 ELSE 0 END) AS DIFF_FECHA_EMISION
,SUM(CASE WHEN PROD.AUDITDATE!=UAT.AUDITDATE THEN 1 ELSE 0 END) AS DIFF_AUDITDATE
,SUM(CASE WHEN PROD.FECHA_PROCESO!=UAT.FECHA_PROCESO THEN 1 ELSE 0 END) AS DIFF_FECHA_PROCESO