SELECT SUM(CASE WHEN PROD.PRID_PARENT!=UAT.PRID_PARENT THEN 1 ELSE 0 END) AS DIFF_PRID_PARENT
,SUM(CASE WHEN TRIM(UPPER(PROD.SIMBOLO_PADRE))!=TRIM(UPPER(UAT.SIMBOLO_PADRE)) THEN 1 ELSE 0 END) AS DIFF_SIMBOLO_PADRE
,SUM(CASE WHEN PROD.TRID_PADRE!=UAT.TRID_PADRE THEN 1 ELSE 0 END) AS DIFF_TRID_PADRE
,SUM(CASE WHEN TRIM(UPPER(PROD.TRDESC_PADRE))!=TRIM(UPPER(UAT.TRDESC_PADRE)) THEN 1 ELSE 0 END) AS DIFF_TRDESC_PADRE
,SUM(CASE WHEN PROD.TRVAL_PADRE!=UAT.TRVAL_PADRE THEN 1 ELSE 0 END) AS DIFF_TRVAL_PADRE
,SUM(CASE WHEN TRIM(UPPER(PROD.TR_DISPLAY_PADRE))!=TRIM(UPPER(UAT.TR_DISPLAY_PADRE)) THEN 1 ELSE 0 END) AS DIFF_TR_DISPLAY_PADRE
,SUM(CASE WHEN PROD.PRID_HIJO!=UAT.PRID_HIJO THEN 1 ELSE 0 END) AS DIFF_PRID_HIJO
,SUM(CASE WHEN TRIM(UPPER(PROD.SIMBOLO_HIJO))!=TRIM(UPPER(UAT.SIMBOLO_HIJO)) THEN 1 ELSE 0 END) AS DIFF_SIMBOLO_HIJO
,SUM(CASE WHEN PROD.TRID_HIJO!=UAT.TRID_HIJO THEN 1 ELSE 0 END) AS DIFF_TRID_HIJO
,SUM(CASE WHEN PROD.COUNT_HIJO!=UAT.COUNT_HIJO THEN 1 ELSE 0 END) AS DIFF_COUNT_HIJO
,SUM(CASE WHEN TRIM(UPPER(PROD.TRDESC_HIJO))!=TRIM(UPPER(UAT.TRDESC_HIJO)) THEN 1 ELSE 0 END) AS DIFF_TRDESC_HIJO
,SUM(CASE WHEN PROD.TRVAL_HIJO!=UAT.TRVAL_HIJO THEN 1 ELSE 0 END) AS DIFF_TRVAL_HIJO
,SUM(CASE WHEN TRIM(UPPER(PROD.TR_DISPLAY_HIJO))!=TRIM(UPPER(UAT.TR_DISPLAY_HIJO)) THEN 1 ELSE 0 END) AS DIFF_TR_DISPLAY_HIJO
,SUM(CASE WHEN PROD.FECHA_PROCESO!=UAT.FECHA_PROCESO THEN 1 ELSE 0 END) AS DIFF_FECHA_PROCESO