,SUM(CASE WHEN TRIM(UPPER(PROD.FACEBOOKIDVALUE))!=TRIM(UPPER(UAT.FACEBOOKIDVALUE)) THEN 1 ELSE 0 END) AS DIFF_FACEBOOKIDVALUE
