SELECT SUM(CASE WHEN PROD.ID_PRODUCTO!=UAT.ID_PRODUCTO THEN 1 ELSE 0 END) AS DIFF_ID_PRODUCTO
,SUM(CASE WHEN TRIM(UPPER(PROD.NOMBRE_PRODUCTO))!=TRIM(UPPER(UAT.NOMBRE_PRODUCTO)) THEN 1 ELSE 0 END) AS DIFF_NOMBRE_PRODUCTO
,SUM(CASE WHEN TRIM(UPPER(PROD.RAMO))!=TRIM(UPPER(UAT.RAMO)) THEN 1 ELSE 0 END) AS DIFF_RAMO
,SUM(CASE WHEN TRIM(UPPER(PROD.ID_RAMO))!=TRIM(UPPER(UAT.ID_RAMO)) THEN 1 ELSE 0 END) AS DIFF_ID_RAMO
,SUM(CASE WHEN TRIM(UPPER(PROD.SUB_RAMO))!=TRIM(UPPER(UAT.SUB_RAMO)) THEN 1 ELSE 0 END) AS DIFF_SUB_RAMO
,SUM(CASE WHEN TRIM(UPPER(PROD.ID_SUBRAMO))!=TRIM(UPPER(UAT.ID_SUBRAMO)) THEN 1 ELSE 0 END) AS DIFF_ID_SUBRAMO
,SUM(CASE WHEN TRIM(UPPER(PROD.NOMBRE_PRODUCTO_COD_SBS))!=TRIM(UPPER(UAT.NOMBRE_PRODUCTO_COD_SBS)) THEN 1 ELSE 0 END) AS DIFF_NOMBRE_PRODUCTO_COD_SBS
,SUM(CASE WHEN TRIM(UPPER(PROD.TIPO_CONTRATO))!=TRIM(UPPER(UAT.TIPO_CONTRATO)) THEN 1 ELSE 0 END) AS DIFF_TIPO_CONTRATO
,SUM(CASE WHEN TRIM(UPPER(PROD.CODIGORAMOSBS))!=TRIM(UPPER(UAT.CODIGORAMOSBS)) THEN 1 ELSE 0 END) AS DIFF_CODIGORAMOSBS
,SUM(CASE WHEN TRIM(UPPER(PROD.RAMOSBS))!=TRIM(UPPER(UAT.RAMOSBS)) THEN 1 ELSE 0 END) AS DIFF_RAMOSBS
,SUM(CASE WHEN TRIM(UPPER(PROD.TIPO_OPENITEM))!=TRIM(UPPER(UAT.TIPO_OPENITEM)) THEN 1 ELSE 0 END) AS DIFF_TIPO_OPENITEM