SELECT SUM(CASE WHEN TRIM(UPPER(PROD.NUMERO_POLIZA))!=TRIM(UPPER(UAT.NUMERO_POLIZA)) THEN 1 ELSE 0 END) AS DIFF_NUMERO_POLIZA
,SUM(CASE WHEN PROD.NUMERO_PRESTAMO!=UAT.NUMERO_PRESTAMO THEN 1 ELSE 0 END) AS DIFF_NUMERO_PRESTAMO
,SUM(CASE WHEN TRIM(UPPER(PROD.MONTO_INICIAL))!=TRIM(UPPER(UAT.MONTO_INICIAL)) THEN 1 ELSE 0 END) AS DIFF_MONTO_INICIAL
,SUM(CASE WHEN TRIM(UPPER(PROD.AMORTIZACIONPENDIENTE))!=TRIM(UPPER(UAT.AMORTIZACIONPENDIENTE)) THEN 1 ELSE 0 END) AS DIFF_AMORTIZACIONPENDIENTE
,SUM(CASE WHEN TRIM(UPPER(PROD.INTERESPENDIENTE))!=TRIM(UPPER(UAT.INTERESPENDIENTE)) THEN 1 ELSE 0 END) AS DIFF_INTERESPENDIENTE
,SUM(CASE WHEN TRIM(UPPER(PROD.TOTALPENDIENTE))!=TRIM(UPPER(UAT.TOTALPENDIENTE)) THEN 1 ELSE 0 END) AS DIFF_TOTALPENDIENTE
,SUM(CASE WHEN PROD.FECHA_INICIAL!=UAT.FECHA_INICIAL THEN 1 ELSE 0 END) AS DIFF_FECHA_INICIAL
,SUM(CASE WHEN PROD.FECHA_FINAL!=UAT.FECHA_FINAL THEN 1 ELSE 0 END) AS DIFF_FECHA_FINAL
,SUM(CASE WHEN PROD.FECHA_PROCESO!=UAT.FECHA_PROCESO THEN 1 ELSE 0 END) AS DIFF_FECHA_PROCESO