SELECT SUM(CASE WHEN TRIM(UPPER(PROD.PK)) IS NULL AND TRIM(UPPER(UAT.PK))="" THEN 1 ELSE 0 END) AS DIFF_PK
,SUM(CASE WHEN TRIM(UPPER(PROD.STATIC)) IS NULL AND TRIM(UPPER(UAT.STATIC))="" THEN 1 ELSE 0 END) AS DIFF_STATIC
,SUM(CASE WHEN TRIM(UPPER(PROD.STATUS)) IS NULL AND TRIM(UPPER(UAT.STATUS))="" THEN 1 ELSE 0 END) AS DIFF_STATUS
,SUM(CASE WHEN TRIM(UPPER(PROD.OPCIONPLANINPUT)) IS NULL AND TRIM(UPPER(UAT.OPCIONPLANINPUT))="" THEN 1 ELSE 0 END) AS DIFF_OPCIONPLANINPUT
,SUM(CASE WHEN TRIM(UPPER(PROD.OPCIONPLANVALUE)) IS NULL AND TRIM(UPPER(UAT.OPCIONPLANVALUE))="" THEN 1 ELSE 0 END) AS DIFF_OPCIONPLANVALUE
,SUM(CASE WHEN TRIM(UPPER(PROD.EDADASEGINPUT)) IS NULL AND TRIM(UPPER(UAT.EDADASEGINPUT))="" THEN 1 ELSE 0 END) AS DIFF_EDADASEGINPUT
,SUM(CASE WHEN TRIM(UPPER(PROD.EDADASEGVALUE)) IS NULL AND TRIM(UPPER(UAT.EDADASEGVALUE))="" THEN 1 ELSE 0 END) AS DIFF_EDADASEGVALUE
,SUM(CASE WHEN TRIM(UPPER(PROD.TARIFESPMIGRINPUT)) IS NULL AND TRIM(UPPER(UAT.TARIFESPMIGRINPUT))="" THEN 1 ELSE 0 END) AS DIFF_TARIFESPMIGRINPUT
,SUM(CASE WHEN TRIM(UPPER(PROD.TARIFESPMIGRVALUE)) IS NULL AND TRIM(UPPER(UAT.TARIFESPMIGRVALUE))="" THEN 1 ELSE 0 END) AS DIFF_TARIFESPMIGRVALUE