SELECT SUM(CASE WHEN TRIM(UPPER(PROD.NUMERO_POLIZA)) IS NULL AND TRIM(UPPER(UAT.NUMERO_POLIZA))="" THEN 1 ELSE 0 END) AS DIFF_NUMERO_POLIZA
,SUM(CASE WHEN TRIM(UPPER(PROD.MONEDA)) IS NULL AND TRIM(UPPER(UAT.MONEDA))="" THEN 1 ELSE 0 END) AS DIFF_MONEDA
,SUM(CASE WHEN TRIM(UPPER(PROD.CODIGO_COBERTURA)) IS NULL AND TRIM(UPPER(UAT.CODIGO_COBERTURA))="" THEN 1 ELSE 0 END) AS DIFF_CODIGO_COBERTURA
,SUM(CASE WHEN TRIM(UPPER(PROD.NOMBRE_COBERTURA)) IS NULL AND TRIM(UPPER(UAT.NOMBRE_COBERTURA))="" THEN 1 ELSE 0 END) AS DIFF_NOMBRE_COBERTURA
,SUM(CASE WHEN TRIM(UPPER(PROD.COBERTURA_PRIMA_NETA)) IS NULL AND TRIM(UPPER(UAT.COBERTURA_PRIMA_NETA))="" THEN 1 ELSE 0 END) AS DIFF_COBERTURA_PRIMA_NETA
,SUM(CASE WHEN TRIM(UPPER(PROD.COBERTURA_PRIMA)) IS NULL AND TRIM(UPPER(UAT.COBERTURA_PRIMA))="" THEN 1 ELSE 0 END) AS DIFF_COBERTURA_PRIMA
,SUM(CASE WHEN TRIM(UPPER(PROD.PAGO)) IS NULL AND TRIM(UPPER(UAT.PAGO))="" THEN 1 ELSE 0 END) AS DIFF_PAGO
,SUM(CASE WHEN TRIM(UPPER(PROD.FRECUENCIA_PAGO)) IS NULL AND TRIM(UPPER(UAT.FRECUENCIA_PAGO))="" THEN 1 ELSE 0 END) AS DIFF_FRECUENCIA_PAGO
,SUM(CASE WHEN TRIM(UPPER(PROD.FECHA_EMISION)) IS NULL AND TRIM(UPPER(UAT.FECHA_EMISION))="" THEN 1 ELSE 0 END) AS DIFF_FECHA_EMISION